// Form handling for adding and editing products

import React from 'react';

const ProductForm = ({ name, setName, quantity, setQuantity, handleSave, isEditing }) => (
    <div>
        <input
            type="text"
            placeholder="Product Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
        />
        <input
            type="number"
            placeholder="Quantity"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
        />

        {/* Add condition to display "Update Product" or "Add Product" button based on isEditing state */}

        {/* Add onClick event handler to handleSave function */}

        {/* Add onClick event handler to reset form fields */}

        {/* Add condition to display "Reset" button */}

        
        <button onClick={handleSave}>
            {isEditing ? 'Update Product' : 'Add Product'}
        </button>
        <button onClick={() => {
            setName('');
            setQuantity(0);
        }}>Reset</button>
    </div>
);

export default ProductForm;
