import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Inventory = () => {
    const [products, setProducts] = useState([]);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState(0);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const res = await axios.get('/api/products'); // Ensure the endpoint is correct
            setProducts(res.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const addProduct = async () => {
        try {
            const product = { name, quantity };
            const res = await axios.post('/api/products', product); // Ensure the endpoint is correct
            setProducts([...products, res.data]);
            setName('');
            setQuantity(0);
            console.log(res.data);
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    const increaseQuantity = async (id) => {
        try {
            await axios.patch(`/api/products/${id}/increase`); // Ensure the endpoint is correct
            fetchProducts();
        } catch (error) {
            console.error('Error increasing quantity:', error);
        }
    };

    const decreaseQuantity = async (id) => {
        try {
            await axios.patch(`/api/products/${id}/decrease`); // Ensure the endpoint is correct
            fetchProducts();
        } catch (error) {
            console.error('Error decreasing quantity:', error);
        }
    };

    const deleteProduct = async (id) => {
        try {
            await axios.delete(`/api/products/${id}`); // Ensure the endpoint is correct
            setProducts(products.filter(product => product._id !== id));
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    const updateProduct = async (id) => {
        try {
            const product = { name, quantity };
            await axios.patch(`/api/products/${id}`, product); // Ensure the endpoint is correct
            fetchProducts();
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };

    const editProduct = (id) => {
        const product = products.find(product => product._id === id);
        setName(product.name);
        setQuantity(product.quantity);
    };

    

    return (
        <div>
            <h1>Inventory Management System</h1>
            <input
                type="text"
                placeholder="Product Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <input
                type="number"
                placeholder="Quantity"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))} // Ensure it's a number
            />
            <button onClick={addProduct}>Add/Update Product</button>
            <ul>
                {products.map(product => (
                    <li key={product._id}>
                        {product.name}: {product.quantity}
                        <button onClick={() => increaseQuantity(product._id)}>Increase</button>
                        <button onClick={() => decreaseQuantity(product._id)}>Decrease</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Inventory;
