import React, { useState, useEffect } from 'react';
import { fetchProducts, addProduct, updateProduct, increaseQuantity, decreaseQuantity, deleteProduct } from '../api';
import ProductForm from './ProductForm';

const ProductList = ({ products, editProduct, deleteProduct }) => {
    return (
        <ul>
            {products.map(product => (
                <li key={product._id}>
                    {product.name}: {product.quantity}
                    <button onClick={() => editProduct(product)}>Edit</button>
                    <button onClick={() => deleteProduct(product._id)}>Delete</button>
                </li>
            ))}
        </ul>
    );
};


const Inventory = () => {
    const [products, setProducts] = useState([]);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState(0);
    const [editingProductId, setEditingProductId] = useState(null);

    useEffect(() => {
        loadProducts();
    }, []);

    const loadProducts = async () => {
        try {
            const products = await fetchProducts();
            setProducts(products);
        } catch (error) {
            console.error('Error loading products:', error);
        }
    };

    const handleSave = async () => {
        try {
            if (editingProductId) {
                await updateProduct(editingProductId, { name, quantity });
            } else {
                const newProduct = await addProduct({ name, quantity });
                setProducts([...products, newProduct]);
            }
            resetForm();
            loadProducts();
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    const editProduct = (product) => {
        setName(product.name);
        setQuantity(product.quantity);
        setEditingProductId(product._id);
    };

    const resetForm = () => {
        setName('');
        setQuantity(0);
        setEditingProductId(null);
    };

    return (
        <div>
            <h1>Inventory Management System</h1>
            <ProductForm
                name={name}
                setName={setName}
                quantity={quantity}
                setQuantity={setQuantity}
                handleSave={handleSave}
                isEditing={!!editingProductId}
            />
            <h2>Products</h2>
            <ProductList
                products={products}
                increaseQuantity={increaseQuantity}
                decreaseQuantity={decreaseQuantity}
                editProduct={editProduct}
                deleteProduct={deleteProduct}
            />
        </div>
    );
};

export default Inventory;
