import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Inventory = () => {
    const [products, setProducts] = useState([]);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState(0);
    const [price, setPrice] = useState(0);
    const [editingProductId, setEditingProductId] = useState(null);


    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const res = await axios.get('/api/products');
            setProducts(res.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const addProduct = async () => {
        try {
            const product = { name, quantity };
            const res = await axios.post('/api/products', product);
            setProducts([...products, res.data]);
            resetForm();
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    const updateProduct = async (id) => {
        try {
            const product = { name, quantity };
            await axios.put(`/api/products/${id}`, product);
            fetchProducts();
            resetForm();
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };

    const increaseQuantity = async (id) => {
        try {
            await axios.patch(`/api/products/${id}/increase`);
            fetchProducts();
        } catch (error) {
            console.error('Error increasing quantity:', error);
        }
    };

    const decreaseQuantity = async (id) => {
        try {
            await axios.patch(`/api/products/${id}/decrease`);
            fetchProducts();
        } catch (error) {
            console.error('Error decreasing quantity:', error);
        }
    };

    const deleteProduct = async (id) => {
        try {
            await axios.delete(`/api/products/${id}`);
            setProducts(products.filter(product => product._id !== id));
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    const editProduct = (product) => {
        setName(product.name);
        setQuantity(product.quantity);
        setEditingProductId(product._id);
    };

    const resetForm = () => {
        setName('');
        setQuantity(0);
        setEditingProductId(null);
    };

    const handleSave = () => {
        if (editingProductId) {
            updateProduct(editingProductId);
        } else {
            addProduct();
        }
    };

    return (
        <div>
            <h1>Inventory Management System</h1>
            <input
                type="text"
                placeholder="Product Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <input
                type="number"
                placeholder="Quantity"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
            />

            {/* Add condition to display "Update Product" or "Add Product" button based on editingProductId state */}

            <input
                type="text"
                placeholder="Product Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <input
                type="number"
                placeholder="Quantity"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
            />

            {/* Add onClick event handler to handleSave function */}


            {/* Add onClick event handler to reset form fields */}


            {/* Add condition to display "Reset" button */}





            <button onClick={handleSave}>
                {editingProductId ? 'Update Product' : 'Add Product'}
            </button>
            <button onClick={resetForm}>Reset</button>
            <h2>Products</h2>
            <ul>
                {products.map(product => (
                    <li key={product._id}>
                        {product.name}: {product.quantity}
                        <button onClick={() => increaseQuantity(product._id)}>Increase</button>
                        <button onClick={() => decreaseQuantity(product._id)}>Decrease</button>
                        <button onClick={() => editProduct(product)}>Edit</button>
                        <button onClick={() => deleteProduct(product._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Inventory;
