import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Inventory = () => {
    const baseUrl = 'http://localhost:5000';
    const [products, setProducts] = useState([]);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState(0);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        const res = await axios.get('/products');
        setProducts(res.data);
    };

    const addProduct = async (product) => {
        try {
            const res = await axios.post('baseUrl/products', product);
            setProducts([...products, res.data]);
        } catch (error) {
            console.log('Error in adding product' + error);
        }

        // await axios.post('/products', { name, quantity });
        // fetchProducts();
    };


    const addProductHandler = async () => {
        await axios.post('/products', { name, quantity });
        fetchProducts();
    };


    const increaseQuantity = async (id) => {
        await axios.patch(`/products/${id}/increase`);
        fetchProducts();
    };

    const decreaseQuantity = async (id) => {
        await axios.patch(`/products/${id}/decrease`);
        fetchProducts();
    };

    return (
        <div>
            <h1>Inventory Management System</h1>
            <input
                type="text"
                placeholder="Product Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <input
                type="number"
                placeholder="Quantity"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
            />
            <button onClick={addProductHandler}>Add/Update Product</button>
            <ul>
                {products.map(product => (
                    <li key={product._id}>
                        {product.name}: {product.quantity}
                        <button onClick={() => increaseQuantity(product._id)}>Increase
                        </button>
                        <button onClick={() => decreaseQuantity(product._id)}>Decrease
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Inventory;
