import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../utils/constants.js';

const Inventory = () => {
    const [products, setProducts] = useState([]);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState(0);

    useEffect(() => {
        const fetchProducts = async () => {
            const res = await axios.get('/products');
            setProducts(res.data);
            console.log(res.data);
        };
        fetchProducts();
    }, [
        setProducts,
        products,
        setName,
        setQuantity,
    ]);


    // const [products, setProducts] = useState([])

  // useEffect(() => {
  //   const fetchProducts = async () => {
  //     const { data } = await axios.get('/api/products')
  //     // const { data } = await response.json()
  //     setProducts(data)
  //   }
    
  //   fetchProducts()
  // }, [])

    // const fetchProducts = async () => {
    //     const res = await axios.get('/products');
    //     setProducts(res.data);
    // };

    const addProduct = async (product) => {

        try {
            const res = await axios.post('http://localhost:5000/products', product);
            setProducts([...products, res.data]);
        } catch (error) {
            console.log('Error in adding product' + error);
        }
    };



    const increaseQuantity = async (id) => {
        await axios.patch(`/products/${id}/increase`);
        fetchProducts();
    };

    const decreaseQuantity = async (id) => {
        await axios.patch(`/products/${id}/decrease`);
        fetchProducts();
    };

    return (
        <div>
            <h1>Inventory Management System</h1>
            <input
                type="text"
                placeholder="Product Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <input
                type="number"
                placeholder="Quantity"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
            />
            <button onClick={addProduct}>Add/Update Product</button>
            <ul>
                {products.map(product => (
                    <li key={product._id}>
                        {product.name}: {product.quantity}
                        <button onClick={() => increaseQuantity(product._id)}>Increase
                        </button>
                        <button onClick={() => decreaseQuantity(product._id)}>Decrease
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Inventory;
