import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Inventory = () => {
    const [products, setProducts] = useState([]);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState(0);

    useEffect(() => {
        // Fetch products from the server when the component mounts
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const res = await axios.get('/api/products'); // Ensure the endpoint is correct
            setProducts(res.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const addProduct = async () => {
        try {
            const product = { name, quantity };
            const res = await axios.post('/api/products', product); // Ensure the endpoint is correct
            setProducts([...products, res.data]);
            setName('');
            setQuantity(0);
            console.log(res.data);
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };


    const updateProduct = async (id) => {
        try {
            const product = { name, quantity };
            await axios.patch(`/api/products/${id}`, product); // Ensure the endpoint is correct
            fetchProducts();
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };


     //Send notification email to user when quantity is zero:
    //  const sendNotification = async (id) => {
    //     try {
    //         await axios.post(`/api/products/sendmail/${id}`); 
    //         if (res.data.quantity === 0) {
    //             alert('Send notification email successfully!');
    //         }
    //         fetchProducts()
    //     } catch (error) {
    //         console.error('Error sending notification:', error);
    //     }
    // };


    const increaseQuantity = async (id) => {
        try {
            await axios.patch(`/api/products/${id}/increase`); 
            fetchProducts();
        } catch (error) {
            console.error('Error increasing quantity:', error);
        }
    };


    const decreaseQuantity = async (id) => {
        try {
            await axios.patch(`/api/products/${id}/decrease`); 
            fetchProducts();
            // sendNotification(id);
        } catch (error) {
            console.error('Error decreasing quantity:', error);
        }
    };


    const deleteProduct = async (id) => {
        try {
            await axios.delete(`/api/products/${id}`); // Ensure the endpoint is correct
            setProducts(products.filter(product => product._id !== id));
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };



    const editProduct = (id) => {
        const product = products.find(product => product._id === id);
        setName(product.name);
        setQuantity(product.quantity);
    };

    const saveProduct = (id) => {
        updateProduct(id);
        setName('');
        setQuantity(0);
    };


    return (
        <div>
            <h1>Inventory Management System</h1>
            <input
                type="text"
                placeholder="Product Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <input
                type="number"
                placeholder="Quantity"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))} // Ensure it's a number
            />
            <button onClick={addProduct}>Add Product</button>
            <button onClick={fetchProducts}>Refresh</button>
            <h2>Products</h2>
            <ul>
                {products.map(product => (
                    <li key={product._id}>
                        {product.name}: {product.quantity}
                        {/* <button onClick={() => sendNotification(product._id)}>Send Notification</button> */}
                        {/* <button onClick={{()=> updateProduct(product._id)}}>Update</button> */}
                        <button onClick={() => increaseQuantity(product._id)}>Increase</button>
                        <button onClick={() => decreaseQuantity(product._id)}>Decrease</button>
                        <button onClick={() => editProduct(product._id)}>Edit</button>
                        <button onClick={() => saveProduct(product._id)}>Save</button>
                        <button onClick={() => deleteProduct(product._id)}>Delete</button> 
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Inventory;
