import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../styles/myStyles.css';

const Inventory = () => {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState(0);
  const [editingProductId, setEditingProductId] = useState(null);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('/api/products');
      setProducts(res.data);
    } catch (error) {
      console.error('Error fetching products:', error);
      toast.error('Error fetching products');
    }
  };

  const sendNotification = async (productId) => {
    try {
      const res = await axios.post(`/send-email/${productId}`);
      toast.success(res.data.message);
    } catch (error) {
      console.error('Error sending notification email:', error);
      toast.error('Error sending notification email');
    }
  };

  const validateForm = () => {
    const errors = {};
    if (name.trim().length < 3) {
      errors.name = 'Name is required and must be at least 3 characters long';
      toast.error('Name is required and must be at least 3 characters long');
    }
    if (quantity < 0) {
      errors.quantity = 'Quantity is required and must be a non-negative number';
      toast.error('Quantity is required and must be a non-negative number');
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const addProduct = async () => {
    if (!validateForm()) return;

    try {
      const product = { name, quantity };
      const res = await axios.post('/api/products', product);
      setProducts([...products, res.data]);
      resetForm();
      toast.success('Product added successfully');
    } catch (error) {
      console.error('Error adding product:', error);
      toast.error('Error adding product');
    }
  };

  const updateProduct = async (id) => {
    if (!validateForm()) return;

    try {
      const product = { name, quantity };
      await axios.put(`/api/products/${id}`, product);
      fetchProducts();
      resetForm();
      toast.success('Product updated successfully');
    } catch (error) {
      console.error('Error updating product:', error);
      toast.error('Error updating product');
    }
  };

  const increaseQuantity = async (id) => {
    try {
      await axios.patch(`/api/products/${id}/increase`);
      fetchProducts();
      toast.success('Quantity increased successfully');
    } catch (error) {
      console.error('Error increasing quantity:', error);
      toast.error('Error increasing quantity');
    }
  };

  const decreaseQuantity = async (id) => {
    try {
      await axios.patch(`/api/products/${id}/decrease`);
      fetchProducts();
      toast.success('Quantity decreased successfully');
    } catch (error) {
      console.error('Error decreasing quantity:', error);
      toast.error('Error decreasing quantity');
    }
  };

  const deleteProduct = async (id) => {
    try {
      await axios.delete(`/api/products/${id}`);
      setProducts(products.filter((product) => product._id !== id));
      toast.success('Product deleted successfully');
    } catch (error) {
      console.error('Error deleting product:', error);
      toast.error('Error deleting product');
    }
  };

  const editProduct = (product) => {
    setName(product.name);
    setQuantity(product.quantity);
    setEditingProductId(product._id);
  };

  const resetForm = () => {
    setName('');
    setQuantity(0);
    setEditingProductId(null);
    setErrors({});
  };

  const handleSave = () => {
    if (editingProductId) {
      updateProduct(editingProductId);
    } else {
      addProduct();
    }
  };

  return (
    <div className="container">
      <ToastContainer />
      <h1 className="header">Inventory Management System</h1>
      <div className="form-group">
        <label className="label" htmlFor="productName">Product Name</label>
        <input
          type="text"
          id="productName"
          placeholder="Product Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="input"
        />
        {errors.name && <div className="error">{errors.name}</div>}
        <label className="label" htmlFor="quantity">Quantity</label>
        
        <input
          type="number"
          id="quantity"
          placeholder="Quantity"
          value={quantity}
          required
          onChange={(e) => setQuantity(Number(e.target.value))}
          className="input"
        />
        {errors.quantity && <div className="error">{errors.quantity}</div>}
      </div>

      <div className="button-group">

        <button
          onClick={handleSave}
          className={`button ${editingProductId ? 'button-primary' : 'button-success'}`}
        >
          {editingProductId ? 'Update Product' : 'Add Product'}
        </button>

        <button
          onClick={resetForm}
          className="button button-secondary"
        >
          Reset
        </button>

        <button
          onClick={fetchProducts}
          className="button button-success"
        >
          Refresh
        </button>
      </div>
      <h2 className="header">Products</h2>
      <ul className="product-list">
        {products.map((product) => (
          <li key={product._id} className="product-item">
            <div className="product-details">
              <span><span className="label">Name:</span> {product.name}</span>
              <span><span className="label">Quantity:</span> {product.quantity}</span>
            </div>

            <div className="button-group">

              <button
                onClick={() => increaseQuantity(product._id)}
                className="button button-primary"
              >
                Increase
              </button>

              <button
                onClick={() => decreaseQuantity(product._id)}
                className="button button-warning"
              >
                Decrease
              </button>

              <button
                onClick={() => editProduct(product)}
                className="button button-secondary"
              >
                Edit
              </button>

              <button
                onClick={() => deleteProduct(product._id)}
                className="button button-danger"
              >
                Delete
              </button>

              <button
                onClick={() => sendNotification(product._id)}
                className="button button-success"
                disabled={product.quantity !== 0}
              >
                Send Notification
              </button>

            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Inventory;
