import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, List, ListItem, ListItemText, IconButton, Box } from '@mui/material';
import { Add, Edit, Delete, Refresh, ArrowUpward, ArrowDownward } from '@mui/icons-material';

const Inventory = () => {
    const [products, setProducts] = useState([]);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState(0);
    const [editingProductId, setEditingProductId] = useState(null);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const res = await axios.get('/api/products');
            setProducts(res.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const addProduct = async () => {
        try {
            const product = { name, quantity };
            const res = await axios.post('/api/products', product);
            setProducts([...products, res.data]);
            resetForm();
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    const updateProduct = async (id) => {
        try {
            const product = { name, quantity };
            await axios.put(`/api/products/${id}`, product);
            fetchProducts();
            resetForm();
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };

    const increaseQuantity = async (id) => {
        try {
            await axios.patch(`/api/products/${id}/increase`);
            fetchProducts();
        } catch (error) {
            console.error('Error increasing quantity:', error);
        }
    };

    const decreaseQuantity = async (id) => {
        try {
            await axios.patch(`/api/products/${id}/decrease`);
            fetchProducts();
        } catch (error) {
            console.error('Error decreasing quantity:', error);
        }
    };

    const deleteProduct = async (id) => {
        try {
            await axios.delete(`/api/products/${id}`);
            setProducts(products.filter(product => product._id !== id));
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    const editProduct = (product) => {
        setName(product.name);
        setQuantity(product.quantity);
        setEditingProductId(product._id);
    };

    const resetForm = () => {
        setName('');
        setQuantity(0);
        setEditingProductId(null);
    };

    const handleSave = () => {
        if (editingProductId) {
            updateProduct(editingProductId);
        } else {
            addProduct();
        }
    };

    return (
        <Container maxWidth="sm" style={{ marginTop: '2rem' }}>
            <Typography variant="h4" align="center" gutterBottom>
                Inventory Management System
            </Typography>
            <Box component="form" noValidate autoComplete="off" sx={{ mb: 4 }}>
                <TextField
                    fullWidth
                    label="Product Name"
                    variant="outlined"
                    margin="normal"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
                <TextField
                    fullWidth
                    label="Quantity"
                    variant="outlined"
                    margin="normal"
                    type="number"
                    value={quantity}
                    onChange={(e) => setQuantity(Number(e.target.value))}
                />
                <Box display="flex" justifyContent="space-between" mt={2}>
                    <Button
                        variant="contained"
                        color="primary"
                        startIcon={<Add />}
                        onClick={handleSave}
                    >
                        {editingProductId ? 'Update Product' : 'Add Product'}
                    </Button>
                    <Button
                        variant="outlined"
                        color="secondary"
                        onClick={resetForm}
                    >
                        Reset
                    </Button>
                    <Button
                        variant="outlined"
                        color="default"
                        startIcon={<Refresh />}
                        onClick={fetchProducts}
                    >
                        Refresh
                    </Button>
                </Box>
            </Box>
            <Typography variant="h5" gutterBottom>
                Products
            </Typography>
            <List>
                {products.map(product => (
                    <ListItem key={product._id} divider>
                        <ListItemText
                            primary={`${product.name}: ${product.quantity}`}
                        />
                        <IconButton edge="end" color="primary" onClick={() => increaseQuantity(product._id)}>
                            <ArrowUpward />
                        </IconButton>
                        <IconButton edge="end" color="secondary" onClick={() => decreaseQuantity(product._id)}>
                            <ArrowDownward />
                        </IconButton>
                        <IconButton edge="end" color="default" onClick={() => editProduct(product)}>
                            <Edit />
                        </IconButton>
                        <IconButton edge="end" color="error" onClick={() => deleteProduct(product._id)}>
                            <Delete />
                        </IconButton>
                    </ListItem>
                ))}
            </List>
        </Container>
    );
};

export default Inventory;
